import java.io.* 
import jxl.* 
import jxl.write.* 
import java.text.SimpleDateFormat
import groovy.json.JsonSlurper
import groovy.json.JsonOutput

def accessToken = "c553911ac0a287f9a8d1b15510345547ad946562";


def get = new URL("http://rest-api.upskills.in/api/rest/customerorders/limit/10/page/1").openConnection();
get.setRequestProperty("Authorization", "Bearer "+ accessToken)

def postRC = get.getResponseCode();
def responseText = messageExchange.response.responseContent
def httpResponseHeaders = messageExchange.getResponseHeaders()
log.info (responseText)
def slurper = new JsonSlurper()
def content = slurper.parseText(responseText)

log.info(content.data.order_id);
log.info(httpResponseHeaders.x-total-count);

def order_id =[]
def totalcount = []
order_id = content.data.order_id
totalcount = httpResponseHeaders.x-total-count

log.info(order_id)
log.info(totalcount)

get.disconnect()

